contracts/BattlePetsNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./GameToken.sol";

contract BattlePetsNFT is ERC721, ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;

    // 宠物属性结构
    struct Pet {
        uint16 attack;      // 攻击力
        uint16 defense;     // 防御力
        uint16 speed;       // 速度
        uint16 hp;          // 生命值
        uint8 level;        // 等级
        uint8 rarity;       // 稀有度 (1-5)
        uint8[] skills;     // 技能ID数组
        uint256 lastTrainingTime; // 上次训练时间
        uint256 lastBattleTime;   // 上次战斗时间
    }

    // 稀有度概率 (百分比)
    uint8[5] public rarityChances = [50, 30, 15, 4, 1]; // 普通、稀有、史诗、传说、神话

    // 映射：tokenId => 宠物属性
    mapping(uint256 => Pet) public pets;

    // 映射：用户地址 => 拥有的宠物数量
    mapping(address => uint256) public userPetCount;

    // 游戏代币合约
    GameToken public gameToken;

    // 训练费用
    uint256 public trainingCost = 10 * 10**18; // 10个代币
    uint256 public upgradeCost = 50 * 10**18;  // 50个代币

    // 事件
    event PetMinted(address indexed owner, uint256 indexed tokenId, uint8 rarity);
    event PetTrained(uint256 indexed tokenId, uint16 attack, uint16 defense, uint16 speed, uint16 hp);
    event PetUpgraded(uint256 indexed tokenId, uint8 newLevel, uint8 newSkill);

    constructor(address _gameToken) ERC721("Battle Pets", "BPETS") Ownable() {
        gameToken = GameToken(_gameToken);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    // 铸造宠物（免费）
    function mintPet() external returns (uint256) {
        require(userPetCount[msg.sender] < 5, unicode"最多只能拥有5只宠物");

        _tokenIds.increment();
        uint256 newTokenId = _tokenIds.current();

        // 生成随机属性
        uint8 rarity = _generateRarity();
        Pet memory newPet = _generatePetAttributes(rarity);

        pets[newTokenId] = newPet;
        userPetCount[msg.sender]++;

        _mint(msg.sender, newTokenId);

        emit PetMinted(msg.sender, newTokenId, rarity);
        return newTokenId;
    }

    // 训练宠物
    function trainPet(uint256 tokenId) external {
        require(_exists(tokenId), unicode"宠物不存在");
        require(ownerOf(tokenId) == msg.sender, unicode"不是宠物主人");
        require(block.timestamp >= pets[tokenId].lastTrainingTime + 1 hours, unicode"训练冷却中");

        // 检查代币余额
        require(gameToken.balanceOf(msg.sender) >= trainingCost, unicode"代币不足");

        // 扣除代币
        gameToken.transferFrom(msg.sender, address(this), trainingCost);

        // 提升属性
        Pet storage pet = pets[tokenId];
        pet.attack += uint16(_random(1, 5));
        pet.defense += uint16(_random(1, 5));
        pet.speed += uint16(_random(1, 3));
        pet.hp += uint16(_random(5, 15));
        pet.lastTrainingTime = block.timestamp;

        emit PetTrained(tokenId, pet.attack, pet.defense, pet.speed, pet.hp);
    }

    // 升级宠物
    function upgradePet(uint256 tokenId) external {
        require(_exists(tokenId), unicode"宠物不存在");
        require(ownerOf(tokenId) == msg.sender, unicode"不是宠物主人");

        Pet storage pet = pets[tokenId];
        require(pet.level < 100, unicode"已达到最高等级");

        // 检查代币余额
        require(gameToken.balanceOf(msg.sender) >= upgradeCost, unicode"代币不足");

        // 扣除代币
        gameToken.transferFrom(msg.sender, address(this), upgradeCost);

        // 升级
        pet.level++;

        // 随机获得新技能
        if (_random(1, 100) <= 30) { // 30%概率获得新技能
            uint8 newSkill = uint8(_random(1, 20));
            pet.skills.push(newSkill);
        }

        emit PetUpgraded(tokenId, pet.level, pet.skills.length > 0 ? pet.skills[pet.skills.length - 1] : 0);
    }

    // 获取宠物属性
    function getPet(uint256 tokenId) external view returns (Pet memory) {
        require(_exists(tokenId), unicode"宠物不存在");
        return pets[tokenId];
    }

    // 生成稀有度
    function _generateRarity() internal view returns (uint8) {
        uint256 rand = _random(1, 100);
        uint256 cumulative = 0;

        for (uint8 i = 0; i < 5; i++) {
            cumulative += rarityChances[i];
            if (rand <= cumulative) {
                return i + 1;
            }
        }
        return 1; // 默认普通
    }

    // 生成宠物属性
    function _generatePetAttributes(uint8 rarity) internal view returns (Pet memory) {
        uint16 baseMultiplier = uint16(rarity);

        return Pet({
            attack: uint16(_random(10, 20) * baseMultiplier),
            defense: uint16(_random(8, 15) * baseMultiplier),
            speed: uint16(_random(5, 12) * baseMultiplier),
            hp: uint16(_random(50, 100) * baseMultiplier),
            level: 1,
            rarity: rarity,
            skills: new uint8[](0),
            lastTrainingTime: 0,
            lastBattleTime: 0
        });
    }

    // 简单随机数生成（生产环境建议使用Chainlink VRF）
    function _random(uint256 min, uint256 max) internal view returns (uint256) {
        return uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender))) % (max - min + 1) + min;
    }

    // 设置训练费用
    function setTrainingCost(uint256 _cost) external onlyOwner {
        trainingCost = _cost;
    }

    // 设置升级费用
    function setUpgradeCost(uint256 _cost) external onlyOwner {
        upgradeCost = _cost;
    }

    // 重写必要的函数
    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/BattleSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./BattlePetsNFT.sol";
import "./GameToken.sol";

contract BattleSystem is Ownable {

    BattlePetsNFT public petsNFT;
    GameToken public gameToken;

    // 战斗费用
    uint256 public battleFee = 5 * 10**18; // 5个代币

    // 战斗奖励
    uint256 public winnerReward = 10 * 10**18; // 10个代币
    uint256 public loserReward = 2 * 10**18;   // 2个代币

    // 战斗状态
    enum BattleStatus { Pending, InProgress, Finished }

    struct Battle {
        uint256 battleId;
        address player1;
        uint256 pet1Id;
        address player2;
        uint256 pet2Id;
        BattleStatus status;
        address winner;
        uint256 startTime;
        uint256 endTime;
        uint256 rounds;
    }

    // 战斗记录
    mapping(uint256 => Battle) public battles;
    uint256 public battleCount;

    // 等待匹配的玩家
    struct MatchmakingEntry {
        address player;
        uint256 petId;
        uint256 entryTime;
        uint256 entryFee;
    }

    MatchmakingEntry[] public matchmakingQueue;

    // 事件
    event BattleStarted(uint256 indexed battleId, address player1, address player2, uint256 pet1Id, uint256 pet2Id);
    event BattleFinished(uint256 indexed battleId, address winner, uint256 rounds);
    event PlayerQueued(address indexed player, uint256 petId, uint256 entryFee);

    constructor(address _petsNFT, address _gameToken) Ownable() {
        petsNFT = BattlePetsNFT(_petsNFT);
        gameToken = GameToken(_gameToken);
    }

    // 进入匹配队列
    function enterMatchmaking(uint256 petId) external {
        require(petsNFT.ownerOf(petId) == msg.sender, unicode"不是宠物主人");
        require(gameToken.balanceOf(msg.sender) >= battleFee, unicode"代币不足");

        // 扣除入场费
        gameToken.transferFrom(msg.sender, address(this), battleFee);

        // 添加到匹配队列
        matchmakingQueue.push(MatchmakingEntry({
            player: msg.sender,
            petId: petId,
            entryTime: block.timestamp,
            entryFee: battleFee
        }));

        emit PlayerQueued(msg.sender, petId, battleFee);

        // 尝试匹配
        _tryMatchmaking();
    }

    // 尝试匹配玩家
    function _tryMatchmaking() internal {
        if (matchmakingQueue.length >= 2) {
            // 简单的FIFO匹配
            MatchmakingEntry memory player1 = matchmakingQueue[0];
            MatchmakingEntry memory player2 = matchmakingQueue[1];

            // 移除前两个玩家
            _removeFromQueue(0);
            _removeFromQueue(0);

            // 开始战斗
            _startBattle(player1.player, player1.petId, player2.player, player2.petId);
        }
    }

    // 从队列中移除玩家
    function _removeFromQueue(uint256 index) internal {
        require(index < matchmakingQueue.length, unicode"索引超出范围");

        for (uint256 i = index; i < matchmakingQueue.length - 1; i++) {
            matchmakingQueue[i] = matchmakingQueue[i + 1];
        }
        matchmakingQueue.pop();
    }

    // 开始战斗
    function _startBattle(address player1, uint256 pet1Id, address player2, uint256 pet2Id) internal {
        battleCount++;

        Battle memory newBattle = Battle({
            battleId: battleCount,
            player1: player1,
            pet1Id: pet1Id,
            player2: player2,
            pet2Id: pet2Id,
            status: BattleStatus.InProgress,
            winner: address(0),
            startTime: block.timestamp,
            endTime: 0,
            rounds: 0
        });

        battles[battleCount] = newBattle;

        emit BattleStarted(battleCount, player1, player2, pet1Id, pet2Id);

        // 执行战斗逻辑
        _executeBattle(battleCount);
    }

    // 执行战斗
    function _executeBattle(uint256 battleId) internal {
        Battle storage battle = battles[battleId];
        BattlePetsNFT.Pet memory pet1 = petsNFT.getPet(battle.pet1Id);
        BattlePetsNFT.Pet memory pet2 = petsNFT.getPet(battle.pet2Id);

        uint16 hp1 = pet1.hp;
        uint16 hp2 = pet2.hp;
        uint256 round = 0;

        // 回合制战斗
        while (hp1 > 0 && hp2 > 0 && round < 20) { // 最多20回合
            round++;

            // 宠物1攻击
            if (hp1 > 0) {
                uint256 damage1 = _calculateDamage(pet1.attack, pet2.defense);
                if (damage1 > hp2) {
                    hp2 = 0;
                } else {
                    hp2 -= uint16(damage1);
                }
            }

            // 宠物2攻击
            if (hp2 > 0) {
                uint256 damage2 = _calculateDamage(pet2.attack, pet1.defense);
                if (damage2 > hp1) {
                    hp1 = 0;
                } else {
                    hp1 -= uint16(damage2);
                }
            }
        }

        // 确定胜负
        address winner;
        if (hp1 > 0) {
            winner = battle.player1;
        } else if (hp2 > 0) {
            winner = battle.player2;
        } else {
            // 平局，随机选择获胜者
            winner = _random() % 2 == 0 ? battle.player1 : battle.player2;
        }

        // 更新战斗结果
        battle.winner = winner;
        battle.status = BattleStatus.Finished;
        battle.endTime = block.timestamp;
        battle.rounds = round;

        // 分配奖励
        _distributeRewards(battle);

        emit BattleFinished(battleId, winner, round);
    }

    // 计算伤害
    function _calculateDamage(uint16 attack, uint16 defense) internal view returns (uint256) {
        uint256 baseDamage = uint256(attack);
        uint256 defenseReduction = uint256(defense) * 50 / 100; // 防御减少50%伤害

        uint256 finalDamage = baseDamage - defenseReduction;

        // 添加随机因素
        uint256 randomFactor = _random() % 20 + 90; // 90-110%的随机伤害
        finalDamage = finalDamage * randomFactor / 100;

        return finalDamage > 0 ? finalDamage : 1; // 最小伤害为1
    }

    // 分配奖励
    function _distributeRewards(Battle storage battle) internal {
        address winner = battle.winner;
        address loser = winner == battle.player1 ? battle.player2 : battle.player1;

        // 给获胜者奖励
        gameToken.battleReward(winner, winnerReward);

        // 给失败者安慰奖
        gameToken.battleReward(loser, loserReward);
    }

    // 简单随机数生成
    function _random() internal view returns (uint256) {
        return uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender)));
    }

    // 获取匹配队列长度
    function getMatchmakingQueueLength() external view returns (uint256) {
        return matchmakingQueue.length;
    }

    // 获取战斗信息
    function getBattle(uint256 battleId) external view returns (Battle memory) {
        return battles[battleId];
    }

    // 设置战斗费用
    function setBattleFee(uint256 _fee) external onlyOwner {
        battleFee = _fee;
    }

    // 设置奖励
    function setRewards(uint256 _winnerReward, uint256 _loserReward) external onlyOwner {
        winnerReward = _winnerReward;
        loserReward = _loserReward;
    }
}

contracts/GameToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract GameToken is ERC20, Ownable {

    // 最大供应量
    uint256 public constant MAX_SUPPLY = 1000000 * 10**18; // 100万代币

    // 初始分配
    uint256 public constant INITIAL_SUPPLY = 100000 * 10**18; // 10万代币

    // 每日挖矿奖励
    uint256 public dailyMiningReward = 1000 * 10**18; // 1000代币/天

    // 用户每日挖矿记录
    mapping(address => uint256) public lastMiningTime;

    // 事件
    event TokensMined(address indexed user, uint256 amount);
    event DailyRewardUpdated(uint256 newAmount);

    constructor() ERC20("Battle Pets Token", "BPT") Ownable() {
        // 初始铸造给合约部署者
        _mint(msg.sender, INITIAL_SUPPLY);
    }

    // 每日挖矿
    function mineDaily() external {
        require(block.timestamp >= lastMiningTime[msg.sender] + 1 days, unicode"需要等待24小时");
        require(totalSupply() + dailyMiningReward <= MAX_SUPPLY, unicode"超过最大供应量");

        lastMiningTime[msg.sender] = block.timestamp;
        _mint(msg.sender, dailyMiningReward);

        emit TokensMined(msg.sender, dailyMiningReward);
    }

    // 战斗奖励
    function battleReward(address winner, uint256 amount) external {
        require(msg.sender == owner() || _isBattleContract(msg.sender), unicode"只有战斗合约可以调用");
        require(totalSupply() + amount <= MAX_SUPPLY, unicode"超过最大供应量");

        _mint(winner, amount);
    }

    // 销毁代币
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    // 设置每日挖矿奖励
    function setDailyMiningReward(uint256 _amount) external onlyOwner {
        dailyMiningReward = _amount;
        emit DailyRewardUpdated(_amount);
    }

    // 战斗合约地址映射
    mapping(address => bool) public battleContracts;

    // 设置战斗合约
    function setBattleContract(address _contract) external onlyOwner {
        battleContracts[_contract] = true;
    }

    // 移除战斗合约
    function removeBattleContract(address _contract) external onlyOwner {
        battleContracts[_contract] = false;
    }

    // 检查是否为战斗合约
    function _isBattleContract(address _contract) internal view returns (bool) {
        return battleContracts[_contract];
    }

    // 重写transfer函数，添加一些限制
    function transfer(address to, uint256 amount) public override returns (bool) {
        require(to != address(0), unicode"不能转账到零地址");
        return super.transfer(to, amount);
    }

    // 重写transferFrom函数
    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        require(to != address(0), unicode"不能转账到零地址");
        return super.transferFrom(from, to, amount);
    }
}

contracts/Leaderboard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./GameToken.sol";

contract Leaderboard is Ownable {

    GameToken public gameToken;

    // 赛季信息
    struct Season {
        uint256 seasonId;
        uint256 startTime;
        uint256 endTime;
        uint256 totalPool;
        bool isActive;
        bool rewardsDistributed;
    }

    // 玩家积分
    struct PlayerScore {
        uint256 score;
        uint256 lastUpdateTime;
        uint256 seasonId;
    }

    // 当前赛季
    uint256 public currentSeasonId = 1;
    uint256 public seasonDuration = 7 days; // 7天一个赛季

    // 赛季映射
    mapping(uint256 => Season) public seasons;

    // 玩家积分映射 (seasonId => address => PlayerScore)
    mapping(uint256 => mapping(address => PlayerScore)) public playerScores;

    // 事件
    event SeasonStarted(uint256 indexed seasonId, uint256 startTime, uint256 endTime);
    event SeasonEnded(uint256 indexed seasonId, uint256 totalPool);
    event ScoreUpdated(address indexed player, uint256 seasonId, uint256 score);
    event RewardsDistributed(uint256 indexed seasonId, address[] winners, uint256[] amounts);

    constructor(address _gameToken) Ownable() {
        gameToken = GameToken(_gameToken);

        // 初始化第一个赛季
        _startNewSeason();
    }

    // 更新玩家积分
    function updateScore(address player, uint256 points) external {
        require(msg.sender == owner() || _isBattleContract(msg.sender), unicode"只有战斗合约可以调用");
        require(seasons[currentSeasonId].isActive, unicode"赛季未开始");

        PlayerScore storage playerScore = playerScores[currentSeasonId][player];

        // 如果是新玩家或新赛季
        if (playerScore.seasonId != currentSeasonId) {
            playerScore.seasonId = currentSeasonId;
            playerScore.score = 0;
        }

        playerScore.score += points;
        playerScore.lastUpdateTime = block.timestamp;

        emit ScoreUpdated(player, currentSeasonId, playerScore.score);

        // 检查是否需要结束赛季
        if (block.timestamp >= seasons[currentSeasonId].endTime) {
            _endSeason();
        }
    }

    // 开始新赛季
    function _startNewSeason() internal {
        Season memory newSeason = Season({
            seasonId: currentSeasonId,
            startTime: block.timestamp,
            endTime: block.timestamp + seasonDuration,
            totalPool: 0,
            isActive: true,
            rewardsDistributed: false
        });

        seasons[currentSeasonId] = newSeason;

        emit SeasonStarted(currentSeasonId, newSeason.startTime, newSeason.endTime);
    }

    // 结束赛季
    function _endSeason() internal {
        require(seasons[currentSeasonId].isActive, unicode"赛季已结束");

        Season storage season = seasons[currentSeasonId];
        season.isActive = false;

        // 计算奖池总额
        season.totalPool = gameToken.balanceOf(address(this));

        emit SeasonEnded(currentSeasonId, season.totalPool);

        // 开始新赛季
        currentSeasonId++;
        _startNewSeason();
    }

    // 手动结束赛季（管理员）
    function endSeason() external onlyOwner {
        require(seasons[currentSeasonId].isActive, unicode"赛季已结束");
        _endSeason();
    }

    // 分配赛季奖励
    function distributeSeasonRewards(uint256 seasonId) external onlyOwner {
        require(seasonId < currentSeasonId, unicode"赛季不存在");
        require(!seasons[seasonId].rewardsDistributed, unicode"奖励已分配");
        require(seasons[seasonId].totalPool > 0, unicode"奖池为空");

        Season storage season = seasons[seasonId];
        season.rewardsDistributed = true;

        // 获取前10%的玩家
        address[] memory topPlayers = _getTopPlayers(seasonId, 10);

        if (topPlayers.length == 0) {
            return;
        }

        // 计算奖励分配
        uint256[] memory rewards = new uint256[](topPlayers.length);
        uint256 totalReward = season.totalPool;

        for (uint256 i = 0; i < topPlayers.length; i++) {
            // 根据排名分配奖励（第一名50%，第二名30%，第三名20%）
            if (i == 0) {
                rewards[i] = totalReward * 50 / 100;
            } else if (i == 1) {
                rewards[i] = totalReward * 30 / 100;
            } else if (i == 2) {
                rewards[i] = totalReward * 20 / 100;
            } else {
                // 其余玩家平分剩余奖励
                uint256 remainingReward = totalReward - rewards[0] - rewards[1] - rewards[2];
                rewards[i] = remainingReward / (topPlayers.length - 3);
            }

            // 发送奖励
            if (rewards[i] > 0) {
                gameToken.transfer(topPlayers[i], rewards[i]);
            }
        }

        emit RewardsDistributed(seasonId, topPlayers, rewards);
    }

    // 获取前N名玩家（简化版，实际应该用更高效的算法）
    function _getTopPlayers(uint256 seasonId, uint256 count) internal view returns (address[] memory) {
        // 这里简化实现，实际应该维护一个排序列表
        // 或者使用链下排序，链上只存储结果
        address[] memory players = new address[](count);
        uint256 playerCount = 0;

        // 注意：这个实现是简化的，实际项目中应该使用更高效的方法
        // 比如维护一个排行榜映射或者使用链下排序

        return players;
    }

    // 获取玩家积分
    function getPlayerScore(address player, uint256 seasonId) external view returns (PlayerScore memory) {
        return playerScores[seasonId][player];
    }

    // 获取当前赛季信息
    function getCurrentSeason() external view returns (Season memory) {
        return seasons[currentSeasonId];
    }

    // 设置赛季时长
    function setSeasonDuration(uint256 _duration) external onlyOwner {
        seasonDuration = _duration;
    }

    // 检查是否为战斗合约
    function _isBattleContract(address _contract) internal view returns (bool) {
        // 这里可以添加战斗合约地址的检查
        return false;
    }

    // 紧急提取代币
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = gameToken.balanceOf(address(this));
        if (balance > 0) {
            gameToken.transfer(owner(), balance);
        }
    }
}

contracts/Marketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./BattlePetsNFT.sol";
import "./GameToken.sol";

contract Marketplace is Ownable, ReentrancyGuard {

    BattlePetsNFT public petsNFT;
    GameToken public gameToken;

    // 交易手续费率 (2%)
    uint256 public feeRate = 200; // 基点 (2% = 200)
    uint256 public constant BASIS_POINTS = 10000;

    // 挂单信息
    struct Listing {
        address seller;
        uint256 tokenId;
        uint256 price;
        uint256 listingTime;
        bool isActive;
    }

    // 拍卖信息
    struct Auction {
        address seller;
        uint256 tokenId;
        uint256 startingPrice;
        uint256 currentBid;
        address highestBidder;
        uint256 endTime;
        bool isActive;
    }

    // 映射：tokenId => 挂单信息
    mapping(uint256 => Listing) public listings;

    // 映射：tokenId => 拍卖信息
    mapping(uint256 => Auction) public auctions;

    // 事件
    event ListingCreated(uint256 indexed tokenId, address indexed seller, uint256 price);
    event ListingCancelled(uint256 indexed tokenId, address indexed seller);
    event ListingSold(uint256 indexed tokenId, address indexed seller, address indexed buyer, uint256 price);
    event AuctionCreated(uint256 indexed tokenId, address indexed seller, uint256 startingPrice, uint256 endTime);
    event BidPlaced(uint256 indexed tokenId, address indexed bidder, uint256 amount);
    event AuctionEnded(uint256 indexed tokenId, address indexed winner, uint256 finalPrice);

    constructor(address _petsNFT, address _gameToken) Ownable() {
        petsNFT = BattlePetsNFT(_petsNFT);
        gameToken = GameToken(_gameToken);
    }

    // 创建挂单
    function createListing(uint256 tokenId, uint256 price) external {
        require(petsNFT.ownerOf(tokenId) == msg.sender, unicode"不是宠物主人");
        require(price > 0, unicode"价格必须大于0");
        require(!listings[tokenId].isActive, unicode"宠物已在挂单中");
        require(!auctions[tokenId].isActive, unicode"宠物已在拍卖中");

        // 转移宠物到市场合约
        petsNFT.transferFrom(msg.sender, address(this), tokenId);

        listings[tokenId] = Listing({
            seller: msg.sender,
            tokenId: tokenId,
            price: price,
            listingTime: block.timestamp,
            isActive: true
        });

        emit ListingCreated(tokenId, msg.sender, price);
    }

    // 取消挂单
    function cancelListing(uint256 tokenId) external nonReentrant {
        Listing storage listing = listings[tokenId];
        require(listing.isActive, unicode"挂单不存在");
        require(listing.seller == msg.sender, unicode"不是挂单创建者");

        listing.isActive = false;

        // 返还宠物
        petsNFT.transferFrom(address(this), msg.sender, tokenId);

        emit ListingCancelled(tokenId, msg.sender);
    }

    // 购买挂单宠物
    function buyListing(uint256 tokenId) external nonReentrant {
        Listing storage listing = listings[tokenId];
        require(listing.isActive, unicode"挂单不存在");
        require(msg.sender != listing.seller, unicode"不能购买自己的宠物");
        require(gameToken.balanceOf(msg.sender) >= listing.price, unicode"代币不足");

        // 扣除代币
        gameToken.transferFrom(msg.sender, address(this), listing.price);

        // 计算手续费
        uint256 fee = (listing.price * feeRate) / BASIS_POINTS;
        uint256 sellerAmount = listing.price - fee;

        // 支付给卖家
        gameToken.transfer(listing.seller, sellerAmount);

        // 转移宠物给买家
        petsNFT.transferFrom(address(this), msg.sender, tokenId);

        // 关闭挂单
        listing.isActive = false;

        emit ListingSold(tokenId, listing.seller, msg.sender, listing.price);
    }

    // 创建拍卖
    function createAuction(uint256 tokenId, uint256 startingPrice, uint256 duration) external {
        require(petsNFT.ownerOf(tokenId) == msg.sender, unicode"不是宠物主人");
        require(startingPrice > 0, unicode"起拍价必须大于0");
        require(duration > 0 && duration <= 7 days, unicode"拍卖时长必须在1-7天之间");
        require(!listings[tokenId].isActive, unicode"宠物已在挂单中");
        require(!auctions[tokenId].isActive, unicode"宠物已在拍卖中");

        // 转移宠物到市场合约
        petsNFT.transferFrom(msg.sender, address(this), tokenId);

        auctions[tokenId] = Auction({
            seller: msg.sender,
            tokenId: tokenId,
            startingPrice: startingPrice,
            currentBid: 0,
            highestBidder: address(0),
            endTime: block.timestamp + duration,
            isActive: true
        });

        emit AuctionCreated(tokenId, msg.sender, startingPrice, block.timestamp + duration);
    }

    // 出价
    function placeBid(uint256 tokenId) external nonReentrant {
        Auction storage auction = auctions[tokenId];
        require(auction.isActive, unicode"拍卖不存在");
        require(block.timestamp < auction.endTime, unicode"拍卖已结束");
        require(msg.sender != auction.seller, unicode"卖家不能出价");

        uint256 minBid = auction.currentBid == 0 ? auction.startingPrice : auction.currentBid + (auction.currentBid * 10 / 100); // 最低加价10%
        require(gameToken.balanceOf(msg.sender) >= minBid, unicode"代币不足");

        // 如果有之前的出价，返还给之前的出价者
        if (auction.highestBidder != address(0)) {
            gameToken.transfer(auction.highestBidder, auction.currentBid);
        }

        // 扣除新出价
        gameToken.transferFrom(msg.sender, address(this), minBid);

        // 更新拍卖信息
        auction.currentBid = minBid;
        auction.highestBidder = msg.sender;

        emit BidPlaced(tokenId, msg.sender, minBid);
    }

    // 结束拍卖
    function endAuction(uint256 tokenId) external nonReentrant {
        Auction storage auction = auctions[tokenId];
        require(auction.isActive, unicode"拍卖不存在");
        require(block.timestamp >= auction.endTime, unicode"拍卖未结束");

        auction.isActive = false;

        if (auction.highestBidder != address(0)) {
            // 有出价者，完成交易
            uint256 fee = (auction.currentBid * feeRate) / BASIS_POINTS;
            uint256 sellerAmount = auction.currentBid - fee;

            // 支付给卖家
            gameToken.transfer(auction.seller, sellerAmount);

            // 转移宠物给最高出价者
            petsNFT.transferFrom(address(this), auction.highestBidder, tokenId);

            emit AuctionEnded(tokenId, auction.highestBidder, auction.currentBid);
        } else {
            // 没有出价者，返还宠物给卖家
            petsNFT.transferFrom(address(this), auction.seller, tokenId);

            emit AuctionEnded(tokenId, address(0), 0);
        }
    }

    // 获取挂单信息
    function getListing(uint256 tokenId) external view returns (Listing memory) {
        return listings[tokenId];
    }

    // 获取拍卖信息
    function getAuction(uint256 tokenId) external view returns (Auction memory) {
        return auctions[tokenId];
    }

    // 设置手续费率
    function setFeeRate(uint256 _feeRate) external onlyOwner {
        require(_feeRate <= 1000, unicode"手续费率不能超过10%");
        feeRate = _feeRate;
    }

    // 提取手续费
    function withdrawFees() external onlyOwner {
        uint256 balance = gameToken.balanceOf(address(this));
        if (balance > 0) {
            gameToken.transfer(owner(), balance);
        }
    }
}

